using System.IO;
using System.Runtime.CompilerServices;

namespace DialogEngine.Models.Logger
{
    /// <summary>
    /// Model for error message
    /// Extends <see cref="LogMessage"/>
    /// </summary>
    public class ErrorMessage : LogMessage
    {
        /// <summary>
        /// Creates instance of ErrorMessage
        /// </summary>
        /// <param name="_message">Message text</param>
        /// <param name="_file">Autogenerated file name</param>
        /// <param name="_line">Autogenerated line in file</param>
        public ErrorMessage(string _message, [CallerFilePath] string _file = "", [CallerLineNumber] int _line = 0) : base(_message)
        {
            SourceFile = Path.GetFileName(_file);
            Line = _line;
        }
    }
}
